n = int(input())
s = input()

def solve():
    for i in range(2**n):
        assignment = bin(i)[2:].zfill(n)
        t = ""
        for j in range(n):
            if assignment[j] == '0':
                t += 'S'
            else:
                t += 'W'

        valid = True
        for j in range(n):
            left = (j - 1 + n) % n
            right = (j + 1) % n

            same_species = (t[left] == t[right])

            if t[j] == 'S':
                if (same_species and s[j] == 'x') or (not same_species and s[j] == 'o'):
                    valid = False
                    break
            else:
                if (same_species and s[j] == 'o') or (not same_species and s[j] == 'x'):
                    valid = False
                    break

        if valid:
            print(t)
            return

    print("-1")

