#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

typedef struct {
    long long x, y;
} Point;

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    Point points[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &points[i].x, &points[i].y);
    }
    long long min_area = LLONG_MAX;
    for (int i = 0; i < (1 << N); i++) {
        if (__builtin_popcount(i) < K) continue;
        long long min_x = LLONG_MAX, max_x = LLONG_MIN, min_y = LLONG_MAX, max_y = LLONG_MIN;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                min_x = (min_x < points[j].x) ? min_x : points[j].x;
                max_x = (max_x > points[j].x) ? max_x : points[j].x;
                min_y = (min_y < points[j].y) ? min_y : points[j].y;
                max_y = (max_y > points[j].y) ? max_y : points[j].y;
            }
        }
        long long area = (max_x - min_x) * (max_y - min_y);
        min_area = (min_area < area) ? min_area : area;
    }
    printf("%lld\n", min_area);
    return 0;
}
