#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    int n;
    char s[100001];
    scanf("%d", &n);
    scanf("%s", s);
    char t[n];
    for (int i = 0; i < (1 << n); i++) {
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1)
                t[j] = 'S';
            else
                t[j] = 'W';
        }
        int flag = 1;
        for (int j = 0; j < n; j++) {
            int prev = (j - 1 + n) % n;
            int next = (j + 1) % n;
            if (t[j] == 'S') {
                if (t[prev] == t[next]) {
                    if (s[j] != 'o')
                        flag = 0;
                } else {
                    if (s[j] != 'x')
                        flag = 0;
                }
            } else {
                if (t[prev] == t[next]) {
                    if (s[j] != 'x')
                        flag = 0;
                } else {
                    if (s[j] != 'o')
                        flag = 0;
                }
            }
        }
        if (flag) {
            for (int j = 0; j < n; j++)
                printf("%c", t[j]);
            printf("\n");
            return 0;
        }
    }
    printf("-1\n");
    return 0;
}
