import java.util.*;

public class Zoo {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        sc.close();

        for (int i = 0; i < (1 << n); i++) {
            StringBuilder t = new StringBuilder();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 0) {
                    t.append('S');
                } else {
                    t.append('W');
                }
            }

            boolean ok = true;
            for (int j = 0; j < n; j++) {
                char c1 = t.charAt(j);
                char c2 = t.charAt((j + 1) % n);
                char c3 = t.charAt((j + n - 1) % n);
                char sj = s.charAt(j);

                boolean same = c2 == c3;
                boolean consistent;
                if (c1 == 'S') {
                    consistent = (same && sj == 'o') || (!same && sj == 'x');
                } else {
                    consistent = (same && sj == 'x') || (!same && sj == 'o');
                }
                if (!consistent) {
                    ok = false;
                    break;
                }
            }
            if (ok) {
                System.out.println(t);
                return;
            }
        }
        System.out.println("-1");
    }
}
