#include <stdio.h>
#include <stdlib.h>

int main() {
    int N;
    scanf("%d", &N);
    long long a[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &a[i]);
    }
    long long count[N];
    for (int i = 0; i < N; i++) {
        count[i] = 0;
    }
    long long total = 0;
    for (int i = 0; i < N; i++) {
        total += a[i];
    }
    long long b[N];
    for (int i = 0; i < N; i++) {
        b[i] = a[i];
    }
    for (long long i = 0; i < total; i++) {
        long long max = -1;
        int index = -1;
        for (int j = 0; j < N; j++) {
            if (b[j] > max) {
                max = b[j];
                index = j;
            } else if (b[j] == max && j < index) {
                index = j;
            }
        }
        count[index]++;
        long long min = -1;
        int min_index = -1;
        for (int j = 0; j < N; j++) {
            if (b[j] > 0) {
                if (min == -1 || b[j] < min) {
                    min = b[j];
                    min_index = j;
                }
            }

        }
        b[min_index]--;

    }
    for (int i = 0; i < N; i++) {
        printf("%lld\n", count[i]);
    }
    return 0;
}
