#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  vector<long long> ans(n, 0);
  vector<long long> b = a;
  vector<pair<long long, int>> cur;
  for (int i = 0; i < n; i++) {
    cur.push_back({b[i], i});
  }

  while (true) {
    long long max_val = -1;
    int min_index = -1;
    for (int i = 0; i < n; ++i) {
      if (cur[i].first > max_val) {
        max_val = cur[i].first;
        min_index = i;
      } else if (cur[i].first == max_val && cur[i].second < min_index) {
        min_index = cur[i].second;
      }
    }
    if (min_index == -1) break;
    ans[min_index]++;
    cur[min_index].first--;
  }

  for (int i = 0; i < n; ++i) {
    cout << ans[i] << endl;
  }
  return 0;
}
