#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    return *(long long *)a - *(long long *)b;
}

int main() {
    int n;
    scanf("%d", &n);
    long long x[n], y[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &x[i], &y[i]);
    }
    long long pos[2 * n];
    for (int i = 0; i < n; i++) {
        pos[2 * i] = x[i];
        pos[2 * i + 1] = y[i];
    }
    qsort(pos, 2 * n, sizeof(long long), cmp);
    long long max_d = 0;
    for (int i = 0; i < (1 << n); i++) {
        long long cur_pos[n];
        for (int j = 0; j < n; j++) {
            if ((i >> j) & 1) {
                cur_pos[j] = y[j];
            } else {
                cur_pos[j] = x[j];
            }
        }
        qsort(cur_pos, n, sizeof(long long), cmp);
        long long min_d = -1;
        for (int j = 1; j < n; j++) {
            long long d = cur_pos[j] - cur_pos[j - 1];
            if (min_d == -1 || d < min_d) {
                min_d = d;
            }
        }
        if (min_d > max_d) {
            max_d = min_d;
        }
    }
    printf("%lld\n", max_d);
    return 0;
}
