#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<pair<long long, long long>> flags(n);
  for (int i = 0; i < n; ++i) {
    cin >> flags[i].first >> flags[i].second;
  }

  long long max_d = 0;
  for (int i = 0; i < (1 << n); ++i) {
    vector<long long> positions;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        positions.push_back(flags[j].first);
      } else {
        positions.push_back(flags[j].second);
      }
    }
    sort(positions.begin(), positions.end());

    long long min_dist = -1;
    for (size_t j = 1; j < positions.size(); ++j) {
      long long dist = positions[j] - positions[j - 1];
      if (min_dist == -1 || dist < min_dist) {
        min_dist = dist;
      }
    }
    max_d = max(max_d, min_dist);
  }
  cout << max_d << endl;
  return 0;
}
