#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    long long n, k;
    cin >> n >> k;

    vector<pair<long long, long long>> points(n);
    for (int i = 0; i < n; ++i) {
        cin >> points[i].first >> points[i].second;
    }

    long long min_area = -1;

    for (int i = 0; i < (1 << n); ++i) {
        if (__builtin_popcount(i) < k) continue;

        vector<pair<long long, long long>> subset;
        for (int j = 0; j < n; ++j) {
            if ((i >> j) & 1) {
                subset.push_back(points[j]);
            }
        }

        long long min_x = subset[0].first;
        long long max_x = subset[0].first;
        long long min_y = subset[0].second;
        long long max_y = subset[0].second;

        for (int j = 1; j < subset.size(); ++j) {
            min_x = min(min_x, subset[j].first);
            max_x = max(max_x, subset[j].first);
            min_y = min(min_y, subset[j].second);
            max_y = max(max_y, subset[j].second);
        }

        long long area = (max_x - min_x) * (max_y - min_y);
        if (min_area == -1 || area < min_area) {
            min_area = area;
        }
    }
    cout << min_area << endl;
    return 0;
}
