import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[][] xy = new long[n][2];
        for (int i = 0; i < n; i++) {
            xy[i][0] = sc.nextLong();
            xy[i][1] = sc.nextLong();
        }
        long maxD = 0;
        for (int i = 0; i < (1 << n); i++) {
            List<Long> pos = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 0) {
                    pos.add(xy[j][0]);
                } else {
                    pos.add(xy[j][1]);
                }
            }
            Collections.sort(pos);
            long minD = Long.MAX_VALUE;
            for (int j = 0; j < n - 1; j++) {
                minD = Math.min(minD, pos.get(j + 1) - pos.get(j));
            }
            maxD = Math.max(maxD, minD);
        }
        System.out.println(maxD);
    }
}
