#include <iostream>
#include <vector>
#include <string>

using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<string> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    vector<string> b(m);
    for (int i = 0; i < m; ++i) {
        cin >> b[i];
    }

    bool found = false;
    for (int i = 0; i <= n - m; ++i) {
        for (int j = 0; j <= n - m; ++j) {
            bool match = true;
            for (int k = 0; k < m; ++k) {
                for (int l = 0; l < m; ++l) {
                    if (a[i + k][j + l] != b[k][l]) {
                        match = false;
                        break;
                    }
                }
                if (!match) break;
            }
            if (match) {
                found = true;
                break;
            }
        }
        if (found) break;
    }

    cout << (found ? "Yes" : "No") << endl;

    return 0;
}
