N, M = map(int, input().split())
A = [input() for _ in range(N)]
B = [input() for _ in range(M)]

found = False
for i in range(N - M + 1):
    for j in range(N - M + 1):
        match = True
        for k in range(M):
            if A[i + k][j:j + M] != B[k]:
                match = False
                break
        if match:
            found = True
            break
    if found:
        break

if found:
    print("Yes")
else:
    print("No")

