#include <stdio.h>
#include <stdbool.h>

int n, m;
bool adj[8][8];
bool visited[8];
int count = 0;

void dfs(int u, int path_len) {
    visited[u] = true;
    if (path_len == n) {
        count++;
        visited[u] = false;
        return;
    }
    for (int v = 1; v <= n; v++) {
        if (adj[u][v] && !visited[v]) {
            dfs(v, path_len + 1);
        }
    }
    visited[u] = false;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        adj[u][v] = adj[v][u] = true;
    }
    dfs(1, 1);
    printf("%d\n", count);
    return 0;
}
