import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        long[] x = new long[n];
        long[] y = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = sc.nextLong();
            y[i] = sc.nextLong();
        }
        long minArea = Long.MAX_VALUE;
        for (int i = 0; i < (1 << n); i++) {
            if (Integer.bitCount(i) < k) continue;
            List<Long> curX = new ArrayList<>();
            List<Long> curY = new ArrayList<>();
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    curX.add(x[j]);
                    curY.add(y[j]);
                }
            }
            long minX = Collections.min(curX);
            long maxX = Collections.max(curX);
            long minY = Collections.min(curY);
            long maxY = Collections.max(curY);
            minArea = Math.min(minArea, (maxX - minX) * (maxY - minY));
        }
        System.out.println(minArea);
    }
}
