#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<tuple<int, int>> edges;
  for (int i = 0; i < m; ++i) {
    int u, v;
    cin >> u >> v;
    edges.emplace_back(u, v);
  }

  vector<int> p(n);
  for (int i = 0; i < n; ++i) {
    p[i] = i + 1;
  }

  long long count = 0;
  do {
    if (p[0] != 1) continue;
    bool ok = true;
    for (int i = 0; i < n - 1; ++i) {
      bool found = false;
      for (const auto& edge : edges) {
        if ((get<0>(edge) == p[i] && get<1>(edge) == p[i + 1]) || (get<0>(edge) == p[i + 1] && get<1>(edge) == p[i])) {
          found = true;
          break;
        }
      }
      if (!found) {
        ok = false;
        break;
      }
    }
    if (ok) count++;
  } while (next_permutation(p.begin(), p.end()));

  cout << count << endl;
  return 0;
}
