import java.util.*;

public class Main {
    static int n, m;
    static List<List<Integer>> graph;
    static boolean[] visited;
    static int count;
    static int[] path;
    static int pathIndex;

    public static void dfs(int u) {
        visited[u] = true;
        path[pathIndex++] = u;
        if (pathIndex == n) {
            count++;
        } else {
            for (int v : graph.get(u)) {
                if (!visited[v]) {
                    dfs(v);
                }
            }
        }
        visited[u] = false;
        pathIndex--;
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt();
        m = sc.nextInt();
        graph = new ArrayList<>();
        for (int i = 0; i <= n; i++) {
            graph.add(new ArrayList<>());
        }
        for (int i = 0; i < m; i++) {
            int u = sc.nextInt();
            int v = sc.nextInt();
            graph.get(u).add(v);
            graph.get(v).add(u);
        }
        visited = new boolean[n + 1];
        path = new int[n];
        pathIndex = 0;
        count = 0;
        dfs(1);
        System.out.println(count);
        sc.close();
    }
}
