#include <stdio.h>
#include <limits.h>

int main() {
    int N, Ma, Mb;
    scanf("%d %d %d", &N, &Ma, &Mb);
    int a[N], b[N], c[N];
    for (int i = 0; i < N; i++) {
        scanf("%d %d %d", &a[i], &b[i], &c[i]);
    }
    int min_cost = INT_MAX;
    for (int i = 0; i < (1 << N); i++) {
        int total_a = 0, total_b = 0, total_cost = 0;
        for (int j = 0; j < N; j++) {
            if ((i >> j) & 1) {
                total_a += a[j];
                total_b += b[j];
                total_cost += c[j];
            }
        }
        if (total_a == 0 || total_b == 0) continue;
        if (total_a * Mb == total_b * Ma) {
            min_cost = (min_cost < total_cost) ? min_cost : total_cost;
        }
    }
    if (min_cost == INT_MAX) {
        printf("-1\n");
    } else {
        printf("%d\n", min_cost);
    }
    return 0;
}

