#include <iostream>
#include <vector>
#include <limits>

using namespace std;

int main() {
  int n, ma, mb;
  cin >> n >> ma >> mb;

  vector<tuple<int, int, int>> chemicals(n);
  for (int i = 0; i < n; ++i) {
    cin >> get<0>(chemicals[i]) >> get<1>(chemicals[i]) >> get<2>(chemicals[i]);
  }

  long long min_cost = numeric_limits<long long>::max();
  for (int i = 0; i < (1 << n); ++i) {
    long long total_a = 0;
    long long total_b = 0;
    long long total_cost = 0;
    for (int j = 0; j < n; ++j) {
      if ((i >> j) & 1) {
        total_a += get<0>(chemicals[j]);
        total_b += get<1>(chemicals[j]);
        total_cost += get<2>(chemicals[j]);
      }
    }

    if (total_a == 0 && total_b == 0) continue;

    if (total_b * ma == total_a * mb && total_a > 0 && total_b >0) {
      min_cost = min(min_cost, total_cost);
    }
  }

  if (min_cost == numeric_limits<long long>::max()) {
    cout << -1 << endl;
  } else {
    cout << min_cost << endl;
  }

  return 0;
}
