import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int ma = sc.nextInt();
        int mb = sc.nextInt();
        int[][] chemicals = new int[n][3];
        for (int i = 0; i < n; i++) {
            chemicals[i][0] = sc.nextInt();
            chemicals[i][1] = sc.nextInt();
            chemicals[i][2] = sc.nextInt();
        }
        long minCost = -1;
        for (int i = 0; i < (1 << n); i++) {
            int totalA = 0;
            int totalB = 0;
            long totalCost = 0;
            for (int j = 0; j < n; j++) {
                if ((i >> j) % 2 == 1) {
                    totalA += chemicals[j][0];
                    totalB += chemicals[j][1];
                    totalCost += chemicals[j][2];
                }
            }
            if (totalA == 0 || totalB == 0) continue;
            if (totalA * mb == totalB * ma) {
                if (minCost == -1 || totalCost < minCost) {
                    minCost = totalCost;
                }
            }
        }
        System.out.println(minCost);
    }
}
