N, Ma, Mb = map(int, input().split())
chemicals = []
for i in range(N):
    a, b, c = map(int, input().split())
    chemicals.append((a, b, c))

min_cost = float('inf')
for i in range(1 << N):
    total_a = 0
    total_b = 0
    total_cost = 0
    for j in range(N):
        if (i >> j) & 1:
            a, b, c = chemicals[j]
            total_a += a
            total_b += b
            total_cost += c
    if total_a == 0 or total_b == 0:
        continue
    if total_a * Mb == total_b * Ma:
        min_cost = min(min_cost, total_cost)

if min_cost == float('inf'):
    print(-1)
else:
    print(min_cost)

