#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    int odd_count = 0;
    int even_count = 0;
    for (int i = 0; i < N; i++) {
        int A;
        scanf("%d", &A);
        if (A % 2 == 0) {
            even_count++;
        } else {
            odd_count++;
        }
    }
    if (odd_count == 0 || even_count == 0 || (odd_count > 0 && even_count > 0 && (odd_count % 2 == 1 && even_count % 2 == 1) || (odd_count % 2 == 0 && even_count % 2 == 0) || (odd_count == 1 && even_count == 1)))
        printf("YES\n");
    else
        printf("NO\n");
    return 0;
}
