#include <iostream>
#include <vector>
#include <numeric>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> a(n);
  int odd_count = 0;
  int even_count = 0;
  long long odd_sum = 0;
  long long even_sum = 0;
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
    if (a[i] % 2 == 0) {
      even_count++;
      even_sum += a[i];
    } else {
      odd_count++;
      odd_sum += a[i];
    }
  }

  if (odd_count == 0 || even_count == 0 || abs(odd_count - even_count) <= 1) {
    cout << "YES" << endl;
  } else {
    cout << "NO" << endl;
  }
  return 0;
}
