import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int odd = 0;
        int even = 0;
        for (int i = 0; i < n; i++) {
            int a = sc.nextInt();
            if (a % 2 == 0) {
                even++;
            } else {
                odd++;
            }
        }
        if (odd == 0 || even == 0 || Math.abs(odd - even) == 1) {
            System.out.println("YES");
        } else {
            System.out.println("NO");
        }
    }
}
