N, K = map(int, input().split())
points = []
for i in range(N):
    x, y = map(int, input().split())
    points.append((x, y))

min_area = float('inf')
for i in range(1 << N):
    subset = []
    for j in range(N):
        if (i >> j) & 1:
            subset.append(points[j])
    if len(subset) < K:
        continue
    x_values = sorted(list(set([p[0] for p in subset])))
    y_values = sorted(list(set([p[1] for p in subset])))
    for x1_index in range(len(x_values)):
        for x2_index in range(x1_index + 1, len(x_values)):
            for y1_index in range(len(y_values)):
                for y2_index in range(y1_index + 1, len(y_values)):
                    count = 0
                    for p in points:
                        if x_values[x1_index] <= p[0] <= x_values[x2_index] and \
                           y_values[y1_index] <= p[1] <= y_values[y2_index]:
                            count += 1
                    if count >= K:
                        area = (x_values[x2_index] - x_values[x1_index]) * \
                               (y_values[y2_index] - y_values[y1_index])
                        min_area = min(min_area, area)

print(min_area)

