#include <stdio.h>
#include <stdlib.h>

int main() {
    int N;
    scanf("%d", &N);
    long long *A = (long long *)malloc(sizeof(long long) * N);
    for (int i = 0; i < N; i++) {
        scanf("%lld", &A[i]);
    }
    for (int i = 0; i < N; i++) {
        long long *B = (long long *)malloc(sizeof(long long) * N);
        for (int j = 0; j < N; j++) {
            B[j] = A[j];
        }
        int flag = 1;
        for (int j = 0; j < N; j++) {
            if (B[j] < j + 1) {
                flag = 0;
                break;
            }
        }
        if (flag == 1) {
            for (int j = 0; j < N; j++) {
                for (int k = 0; k < N; k++) {
                    B[(j + k + 1) % N] -= (k + 1);
                }
            }
            int flag2 = 1;
            for (int j = 0; j < N; j++) {
                if (B[j] != 0) {
                    flag2 = 0;
                    break;
                }
            }
            if (flag2 == 1) {
                printf("YES\n");
                return 0;
            }
        }
    }
    printf("NO\n");
    return 0;
}
