import java.util.*;
import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        for (int i = 0; i < n; i++) {
            long[] b = Arrays.copyOf(a, n);
            boolean possible = true;
            for (int j = 0; j < n; j++) {
                long remove = j + 1;
                int index = (i + j + 1) % n;
                if (b[index] < remove) {
                    possible = false;
                    break;
                }
                b[index] -= remove;

            }
            if (possible) {
                boolean allZero = true;
                for (long x : b) {
                    if (x != 0) {
                        allZero = false;
                        break;
                    }
                }
                if (allZero) {
                    System.out.println("YES");
                    return;
                }
            }
        }
        System.out.println("NO");
    }
}
