#include <stdio.h>
#include <stdlib.h>

#define MAXN 100005

int n;
long long a[MAXN];
int deg[MAXN];
int g[MAXN][2];

int main() {
    scanf("%d", &n);
    long long sum = 0;
    for (int i = 1; i <= n; i++) {
        scanf("%lld", &a[i]);
        sum += a[i];
    }
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        deg[u]++;
        deg[v]++;
        g[u][deg[u] - 1] = v;
        g[v][deg[v] - 1] = u;
    }
    int leaves = 0;
    for (int i = 1; i <= n; i++) {
        if (deg[i] == 1) leaves++;
    }
    if (sum % leaves != 0) {
        printf("NO\n");
        return 0;
    }
    if (leaves == 0) {
        printf("NO\n");
        return 0;
    }

    printf("YES\n");
    return 0;
}
