import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        List<List<Integer>> g = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            g.add(new ArrayList<>());
        }
        for (int i = 0; i < n - 1; i++) {
            int u = sc.nextInt() - 1;
            int v = sc.nextInt() - 1;
            g.get(u).add(v);
            g.get(v).add(u);
        }
        long sum = 0;
        for (long x : a) {
            sum += x;
        }
        if (sum % 2 != 0) {
            System.out.println("NO");
            return;
        }
        int leaves = 0;
        for (int i = 0; i < n; i++) {
            if (g.get(i).size() == 1) {
                leaves++;
            }
        }
        if (leaves <= 1) {
            System.out.println("NO");
            return;
        }
        System.out.println("YES");
    }
}
