#include <stdio.h>
#include <stdlib.h>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    long long min_val = -1;
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
        if (min_val == -1 || a[i] < min_val)
            min_val = a[i];
    }
    if (min_val == 1) {
        printf("Second\n");
        return 0;
    }

    long long moves = 0;
    for (int i = 0; i < n; i++) {
        moves += (a[i] -1);
    }

    if (moves % 2 == 1)
        printf("First\n");
    else
        printf("Second\n");

    return 0;
}
