#include <iostream>
#include <vector>
#include <numeric>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int n;
    std::cin >> n;
    std::vector<long long> a(n);
    long long min_val = -1;
    for (int i = 0; i < n; ++i) {
        std::cin >> a[i];
        if (min_val == -1 || a[i] < min_val)
            min_val = a[i];
    }
    long long moves = 0;
    for (int i = 0; i < n; ++i) {
        moves += a[i] - 1;
    }

    if (moves % 2 == 1)
        std::cout << "First" << std::endl;
    else
        std::cout << "Second" << std::endl;
    return 0;
}
