import math

def gcd(a, b):
  if b == 0:
    return a
  return gcd(b, a % b)

def solve():
  n = int(input())
  a = list(map(int, input().split()))

  nim_sum = 0
  for x in a:
    nim_sum ^= (x - 1)

  if nim_sum == 0:
    print("Second")
  else:
    print("First")

