import java.util.*;

public class Main {
    public static int gcd(int a, int b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        Arrays.sort(a);
        List<Integer> list = new ArrayList<>();
        for (int x : a) list.add(x);
        List<Integer> ans = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            int cur = -1;
            for (int j = 0; j < list.size(); j++) {
                boolean ok = true;
                for (int k = 0; k < ans.size(); k++) {
                    if (gcd(list.get(j), ans.get(k)) == 1) {
                        ok = false;
                        break;
                    }
                }
                if (ok) {
                    if (cur == -1 || list.get(j) > cur) {
                        cur = list.get(j);
                    }
                }
            }
            ans.add(cur);
            list.remove(Integer.valueOf(cur));
        }
        for (int i = 0; i < n; i++) {
            System.out.print(ans.get(i) + (i == n - 1 ? "" : " "));
        }
        System.out.println();
    }
}
