import math

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def solve():
    n = int(input())
    a = list(map(int, input().split()))

    import itertools
    perms = list(itertools.permutations(a))

    best_perm = None

    for perm in perms:
        current_perm = list(perm)
        changed = True
        while changed:
            changed = False
            for i in range(n - 1):
                if gcd(current_perm[i], current_perm[i+1]) == 1:
                    if current_perm[i] > current_perm[i+1]:
                        current_perm[i], current_perm[i+1] = current_perm[i+1], current_perm[i]
                        changed = True

        if best_perm is None or current_perm < best_perm:
            best_perm = current_perm

    print(*best_perm)

solve()
