#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);
    long long a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld", &a[i]);
    }
    int adj[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            adj[i][j] = 0;
        }
    }
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        u--;
        v--;
        adj[u][v] = 1;
        adj[v][u] = 1;
    }
    int win[n];
    for (int i = 0; i < n; i++) {
        win[i] = 0;
    }
    for (int i = 0; i < n; i++) {
        if (a[i] % 2 != 0) {
            win[i] = 1;
        }
    }
    for (int i = 0; i < n; i++) {
        if (win[i] == 1) {
            for (int j = 0; j < n; j++) {
                if (adj[i][j] == 1 && a[j] % 2 == 0) {
                    win[i] = 0;
                    break;
                }
            }
        }
    }
    int first = 0;
    for (int i = 0; i < n; i++) {
        if (win[i] == 1) {
            if (first == 0) {
                printf("%d", i + 1);
                first = 1;
            } else {
                printf(" %d", i + 1);
            }
        }
    }
    printf("\n");
    return 0;
}
