#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<long long> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    vector<vector<int>> adj(n);
    for (int i = 0; i < n - 1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u - 1].push_back(v - 1);
        adj[v - 1].push_back(u - 1);
    }

    vector<int> winning_positions;
    for (int i = 0; i < n; ++i) {
        if (a[i] % 2 != 0) {
            winning_positions.push_back(i + 1);
        }
    }

    sort(winning_positions.begin(), winning_positions.end());
    for (int i = 0; i < winning_positions.size(); ++i) {
        cout << winning_positions[i] << (i == winning_positions.size() - 1 ? "" : " ");
    }
    cout << endl;

    return 0;
}
