#include <stdio.h>
#include <string.h>

int main() {
    char s[200001];
    scanf("%s", s);
    int n = strlen(s);
    int max_len = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'A') {
            for (int j = i + 1; j < n; j++) {
                if (s[j] == 'Z') {
                    max_len = (max_len > (j - i + 1)) ? max_len : (j - i + 1);
                }
            }
        }
    }
    printf("%d\n", max_len);
    return 0;
}
