#include <iostream>
#include <string>

using namespace std;

int main() {
  string s;
  cin >> s;

  int max_len = 0;
  for (int i = 0; i < s.length(); ++i) {
    if (s[i] == 'A') {
      for (int j = i + 1; j < s.length(); ++j) {
        if (s[j] == 'Z') {
          max_len = max(max_len, j - i + 1);
        }
      }
    }
  }
  cout << max_len << endl;
  return 0;
}
