#include <stdio.h>

long long solve() {
    long long x;
    scanf("%lld", &x);
    if (x <= 6) return 1;
    long long rem = x % 14;
    long long div = x / 14;
    long long ans = div * 4;
    if (rem == 0) return ans;
    if (rem <= 6) return ans + 1;
    if (rem <= 10) return ans + 2;
    return ans + 3;

}

int main() {
    printf("%lld\n", solve());
    return 0;
}
